package xlclient.gui;



import java.io.File;
import javax.swing.*;
import javax.swing.filechooser.*;


public class OWLRDFFilter extends FileFilter {

    //Accept all directories and all gif, jpg, tiff, or png files.
    public boolean accept(File f) {
        if (f.isDirectory()) {
            return true;
        }

        String extension = SwingUtil.getExtension(f);
        if (extension != null) {
            if (extension.equals(SwingUtil.owl) ||
                extension.equals(SwingUtil.rdf)) {
                    return true;
            } else {
                return false;
            }
        }

        return false;
    }

    //The description of this filter
    public String getDescription() {
        return "OWL/RDF";
    }
}
